import React, { useState, useEffect } from 'react';
import { getPersonalDetails, updatePersonalDetails } from '../api';
import useAuth from '../hooks/useAuth'; // To display user basic info maybe

const ProfilePage = () => {
  const { user } = useAuth(); // Get logged in user basic info
  const [details, setDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Add state for form inputs (address, dob, pan_no, aadhar_no)
  const [formData, setFormData] = useState({ address: '', dob: '', pan_no: '', aadhar_no: '' });
  const [formError, setFormError] = useState('');
  const [formSuccess, setFormSuccess] = useState('');


  const fetchDetails = async () => {
      setLoading(true);
      setError(null);
      try {
          const res = await getPersonalDetails();
          if (res.data.personal_details) {
              setDetails(res.data.personal_details);
              // Pre-fill form with existing details
              setFormData({
                  address: res.data.personal_details.address || '',
                  dob: res.data.personal_details.dob ? new Date(res.data.personal_details.dob).toISOString().split('T')[0] : '', // Format for input type="date"
                  pan_no: res.data.personal_details.pan_no || '',
                  aadhar_no: res.data.personal_details.aadhar_no || '',
              });
          } else {
               setDetails({}); // No details found yet
          }
      } catch (err) {
           if(err.response?.status === 404) {
               // It's okay if details aren't found initially
               setDetails({});
           } else {
               setError(err.response?.data?.message || 'Failed to fetch personal details');
           }
           setDetails(null); // Indicate error fetching
      } finally {
          setLoading(false);
      }
  };

  useEffect(() => {
    fetchDetails();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setFormError(''); // Clear error on change
    setFormSuccess(''); // Clear success on change
  };

  const handleUpdateSubmit = async (e) => {
      e.preventDefault();
      setFormError('');
      setFormSuccess('');
      // Add validation if needed
      try {
          await updatePersonalDetails(formData);
          setFormSuccess('Personal details updated successfully!');
          fetchDetails(); // Refresh displayed details
      } catch (err) {
          console.error("Update details error:", err);
          setFormError(err.response?.data?.message || 'Failed to update details.');
      }
  };

  if (loading) return <p>Loading profile...</p>;

  return (
    <div>
      <h2>Profile & Personal Details</h2>
       {user && (
            <div>
                <p><strong>Name:</strong> {user.name}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Account No:</strong> {user.account_no}</p>
                <p><strong>Phone:</strong> {user.phone}</p>
            </div>
       )}
      <hr/>

      <h3>Update Personal Details</h3>
       {error && <p style={{ color: 'red' }}>Error loading details: {error}</p>}

       <form onSubmit={handleUpdateSubmit}>
            <div>
                <label htmlFor="address">Address:</label>
                <textarea id="address" name="address" value={formData.address} onChange={handleChange}></textarea>
            </div>
            <div>
                <label htmlFor="dob">Date of Birth:</label>
                <input type="date" id="dob" name="dob" value={formData.dob} onChange={handleChange} />
            </div>
            <div>
                <label htmlFor="pan_no">PAN Number:</label>
                <input type="text" id="pan_no" name="pan_no" value={formData.pan_no} onChange={handleChange} />
            </div>
             <div>
                <label htmlFor="aadhar_no">Aadhar Number:</label>
                <input type="text" id="aadhar_no" name="aadhar_no" value={formData.aadhar_no} onChange={handleChange} />
            </div>

            {formError && <p style={{ color: 'red' }}>{formError}</p>}
            {formSuccess && <p style={{ color: 'green' }}>{formSuccess}</p>}
            <button type="submit">Save Details</button>
       </form>


    </div>
  );
};

export default ProfilePage;
