import React, { useState, useEffect } from 'react';
import {
  getTransactions,
  createTransaction, // Used for logging withdrawals ONLY
  depositAmount,    // Used for actual deposits (updates balance, NO LOG)
  getBalance        // Used to fetch the current balance
} from '../api';

const TransactionsPage = () => {
  const [balance, setBalance] = useState(null);
  const [transactions, setTransactions] = useState([]);

  // Loading states
  const [loadingBalance, setLoadingBalance] = useState(true);
  const [loadingTransactions, setLoadingTransactions] = useState(true);

  // Error states
  const [errorBalance, setErrorBalance] = useState(null);
  const [errorTransactions, setErrorTransactions] = useState(null);

  // Form states
  const [depositAmountValue, setDepositAmountValue] = useState('');
  const [withdrawAmountValue, setWithdrawAmountValue] = useState('');
  const [formDepositError, setFormDepositError] = useState('');
  const [formDepositSuccess, setFormDepositSuccess] = useState('');
  const [formWithdrawalError, setFormWithdrawalError] = useState('');
  const [formWithdrawalSuccess, setFormWithdrawalSuccess] = useState('');

  // --- Fetch Balance Function ---
  const fetchBalance = async () => {
    setLoadingBalance(true);
    setErrorBalance(null);
    try {
      const res = await getBalance();
      const rawBalance = res.data.balance; // Get the raw value

      // *** FIX HERE: Convert to number and validate ***
      const numericBalance = parseFloat(rawBalance); // Attempt conversion

      if (isNaN(numericBalance)) {
        // Handle cases where the API might return something non-numeric unexpectedly
        console.error("Received non-numeric balance from API:", rawBalance);
        throw new Error('Invalid balance data received from server.');
      }

      setBalance(numericBalance); // Set state ONLY with a valid number

    } catch (err) {
      console.error("Fetch balance error:", err);
      // Use err.message if we threw a custom error above
      setErrorBalance(err.response?.data?.message || err.message || 'Failed to fetch balance');
      setBalance(null); // Reset balance on error
    } finally {
      setLoadingBalance(false);
    }
  };

  // --- Fetch Transactions Function ---
  const fetchTransactions = async () => {
    setLoadingTransactions(true);
    setErrorTransactions(null);
    try {
      const res = await getTransactions();
      // Adjust based on your actual API response structure
      setTransactions(res.data.transactions || res.data || []);
    } catch (err) {
      console.error("Fetch transactions error:", err);
      setErrorTransactions(err.response?.data?.message || 'Failed to fetch transactions');
      setTransactions([]);
    } finally {
      setLoadingTransactions(false);
    }
  };

  // --- Fetch initial data on mount ---
  useEffect(() => {
    fetchBalance();
    fetchTransactions();
  }, []);

  // --- Handle Deposit Submission (Uses /deposit ONLY) ---
  const handleDepositSubmit = async (e) => {
    e.preventDefault();
    setFormDepositError('');
    setFormDepositSuccess('');
    const amount = parseFloat(depositAmountValue);

    if (isNaN(amount) || amount <= 0) {
      setFormDepositError('Please enter a valid positive amount.');
      return;
    }

    try {
      // Call /deposit: This updates the balance but DOES NOT log a transaction.
      await depositAmount({ amount });

      setFormDepositSuccess(`Successfully deposited $${amount.toFixed(2)}. Balance updated.`);
      setFormDepositError('Note: This deposit is NOT logged in the transaction history below due to backend limitations.'); // Clarify limitation
      setDepositAmountValue(''); // Clear input
      fetchBalance();         // Refresh balance display
      // DO NOT fetchTransactions() - the /deposit endpoint didn't create one.

    } catch (depositError) {
      console.error("Deposit error:", depositError);
      setFormDepositError(depositError.response?.data?.message || 'Deposit failed.');
    }
  };

  // --- Handle Withdrawal Submission (Uses /transaction ONLY - LOGGING ONLY) ---
  const handleWithdrawSubmit = async (e) => {
      e.preventDefault();
      setFormWithdrawalError('');
      setFormWithdrawalSuccess('');
      const amount = parseFloat(withdrawAmountValue);

      if (isNaN(amount) || amount <= 0) {
          setFormWithdrawalError('Please enter a valid positive amount.');
          return;
      }

      // Frontend Balance Check (Essential)
      if (balance === null) {
          setFormWithdrawalError('Balance information not available.');
          return;
      }
       if (amount > balance) {
          setFormWithdrawalError('Withdrawal amount exceeds available balance.');
          return;
      }

      try {
          // Call /transaction: This logs the withdrawal but DOES NOT update the balance.
          await createTransaction({ transaction_type: 'Withdrawal', amount: amount });

          setFormWithdrawalSuccess(`Withdrawal attempt for $${amount.toFixed(2)} logged successfully in history.`);
          setFormWithdrawalError('IMPORTANT: Your account balance has NOT been changed. This only logs the attempt.'); // Make limitation extremely clear
          setWithdrawAmountValue(''); // Clear input
          fetchTransactions();    // Refresh transaction list to show the logged withdrawal
          // DO NOT fetchBalance() - it wasn't changed by the backend.

      } catch (err) {
          console.error("Withdrawal logging error:", err);
          setFormWithdrawalError(err.response?.data?.message || 'Failed to log withdrawal attempt.');
      }
  };

  return (
    <div>
      <h2>Transactions</h2>

      {/* --- Balance Display --- */}
      <h3>Current Balance</h3>
      {loadingBalance && <p>Loading balance...</p>}
      {errorBalance && <p style={{ color: 'red' }}>Error: {errorBalance}</p>}
      {balance !== null && !loadingBalance && !errorBalance && (
        <p style={{ fontSize: '1.5em', fontWeight: 'bold' }}>
          ${balance.toFixed(2)}
        </p>
      )}
      <hr />

      {/* --- Deposit Form --- */}
      <h3>Make a Deposit</h3>
       <p style={{ fontStyle: 'italic', color: 'orange' }}>
           Note: Deposits update your balance but are **not** recorded in the history below.
       </p>
      <form onSubmit={handleDepositSubmit}>
          <div>
              <label htmlFor="depositAmount">Amount:</label>
              <input
                  type="number"
                  id="depositAmount"
                  value={depositAmountValue}
                  onChange={(e) => setDepositAmountValue(e.target.value)}
                  min="0.01"
                  step="0.01"
                  required
              />
          </div>
          {/* Show both potential messages for clarity */}
          {formDepositSuccess && <p style={{ color: 'green' }}>{formDepositSuccess}</p>}
          {formDepositError && <p style={{ color: formDepositSuccess ? 'orange' : 'red' }}>{formDepositError}</p>}
          <button type="submit">Deposit</button>
      </form>
      <hr />

      {/* --- Withdrawal Form --- */}
      <h3>Log a Withdrawal Attempt</h3>
       <p style={{ fontStyle: 'italic', color: 'red', fontWeight: 'bold' }}>
           WARNING: This action only logs the withdrawal attempt in the history. Your actual account balance WILL NOT decrease.
       </p>
      <form onSubmit={handleWithdrawSubmit}>
            <div>
                <label htmlFor="withdrawAmount">Amount:</label>
                <input
                    type="number"
                    id="withdrawAmount"
                    value={withdrawAmountValue}
                    onChange={(e) => setWithdrawAmountValue(e.target.value)}
                    min="0.01"
                    step="0.01"
                    required
                />
            </div>
             {/* Show both potential messages for clarity */}
            {formWithdrawalSuccess && <p style={{ color: 'green' }}>{formWithdrawalSuccess}</p>}
            {formWithdrawalError && <p style={{ color: formWithdrawalSuccess ? 'orange' : 'red' }}>{formWithdrawalError}</p>}
             {/* Disable if balance is unknown or insufficient */}
            <button type="submit" disabled={balance === null || withdrawAmountValue > balance}>Log Withdrawal Attempt</button>
      </form>
      <hr />

      {/* --- Transaction History --- */}
      <h3>History</h3>
      {loadingTransactions && <p>Loading transaction history...</p>}
      {errorTransactions && <p style={{ color: 'red' }}>Error fetching history: {errorTransactions}</p>}
      {!loadingTransactions && transactions.length === 0 && !errorTransactions && (
           <p>No transactions found (or deposits may not be logged).</p> // Clarify why it might be empty
      )}
      {transactions.length > 0 && (
        <ul>
          {/* Adjust property names (tx.id, tx.transaction_date, etc.) if needed */}
          {transactions.map((tx) => (
             <li key={tx.id || tx.transaction_id}>
                {new Date(tx.transaction_date || tx.created_at || tx.timestamp).toLocaleString()} - {/* Adjust date field */}
                {tx.transaction_type} -
                Amount: ${parseFloat(tx.amount).toFixed(2)}
                {/* Add visual cue for logged-only withdrawal */}
                {tx.transaction_type === 'Withdrawal' && <span style={{ color: 'red', marginLeft: '10px', fontWeight: 'bold' }}>(Logged Only - Balance Unchanged)</span>}
             </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TransactionsPage;
