import React, { useState, useEffect } from 'react';
import { getLockers, createLocker } from '../api';

const LockersPage = () => {
  const [lockers, setLockers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Add state for form inputs (locker_name)
  // Add state for form errors/success messages

  const fetchLockers = async () => {
     setLoading(true);
     setError(null);
     try {
       const res = await getLockers();
       setLockers(res.data.lockers || []);
     } catch (err) {
       setError(err.response?.data?.message || 'Failed to fetch lockers');
       setLockers([]);
     } finally {
       setLoading(false);
     }
   };

  useEffect(() => {
    fetchLockers();
  }, []);

  const handleAddLockerSubmit = async (e) => {
       e.preventDefault();
       // 1. Get locker_name from state
       // 2. Basic validation
       // 3. Call createLocker({ locker_name: ... })
       // 4. Handle success (clear form, show message, refresh list)
       // 5. Handle error (show message)
       console.log("Add locker form submitted - Implement API call");
       // Example: await createLocker({ locker_name: '...' });
       // fetchLockers(); // Refresh list on success
   };

  if (loading) return <p>Loading lockers...</p>;

  return (
    <div>
      <h2>Lockers</h2>

       {/* Add New Locker Form */}
       <h3>Request New Locker</h3>
        <form onSubmit={handleAddLockerSubmit}>
            {/* Add input field for locker_name */}
             {/* Example: */}
            {/* <div><label>Locker Name/Description:</label><input type="text" ... /></div> */}
            {/* Add error/success message display */}
            <button type="submit">Request Locker</button>
        </form>
        <hr/>


      {/* Locker List */}
      <h3>Your Lockers</h3>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {lockers.length > 0 ? (
        <ul>
          {lockers.map((locker) => (
            <li key={locker.id}>
              Locker ID: {locker.id} - Name: {locker.locker_name}
              {/* Add status or other details if available */}
            </li>
          ))}
        </ul>
      ) : (
         !error && <p>No lockers found.</p>
      )}
    </div>
  );
};

export default LockersPage;
