import React, { useState, useEffect } from 'react';
import {
  getTransactions,
  // createTransaction, // Likely no longer needed here unless other features use it
  depositAmount,    // Used for actual deposits (updates balance)
  getBalance,
  withdrawAmount    // Import the CORRECT withdrawal function
} from '../api';

const TransactionsPage = () => {
  const [balance, setBalance] = useState(null);
  const [transactions, setTransactions] = useState([]);

  // Loading states
  const [loadingBalance, setLoadingBalance] = useState(true);
  const [loadingTransactions, setLoadingTransactions] = useState(true);

  // Error states
  const [errorBalance, setErrorBalance] = useState(null);
  const [errorTransactions, setErrorTransactions] = useState(null);

  // Form states
  const [depositAmountValue, setDepositAmountValue] = useState('');
  const [withdrawAmountValue, setWithdrawAmountValue] = useState('');
  const [formDepositError, setFormDepositError] = useState('');
  const [formDepositSuccess, setFormDepositSuccess] = useState('');
  const [formWithdrawalError, setFormWithdrawalError] = useState('');
  const [formWithdrawalSuccess, setFormWithdrawalSuccess] = useState('');

  // --- Fetch Balance Function ---
  const fetchBalance = async () => {
    setLoadingBalance(true);
    setErrorBalance(null);
    try {
      const res = await getBalance();
      const rawBalance = res.data.balance;
      const numericBalance = parseFloat(rawBalance);
      if (isNaN(numericBalance)) {
        throw new Error('Invalid balance data received from server.');
      }
      setBalance(numericBalance);
    } catch (err) {
      console.error("Fetch balance error:", err);
      setErrorBalance(err.response?.data?.message || err.message || 'Failed to fetch balance');
      setBalance(null);
    } finally {
      setLoadingBalance(false);
    }
  };

  // --- Fetch Transactions Function ---
  const fetchTransactions = async () => {
    setLoadingTransactions(true);
    setErrorTransactions(null);
    try {
      const res = await getTransactions();
      setTransactions(res.data.transactions || res.data || []);
    } catch (err) {
      console.error("Fetch transactions error:", err);
      setErrorTransactions(err.response?.data?.message || 'Failed to fetch transactions');
      setTransactions([]);
    } finally {
      setLoadingTransactions(false);
    }
  };

  // --- Fetch initial data on mount ---
  useEffect(() => {
    fetchBalance();
    fetchTransactions();
  }, []);

    // --- Handle Deposit Submission ---
    const handleDepositSubmit = async (e) => {
      e.preventDefault();
      setFormDepositError('');
      setFormDepositSuccess('');
      const amount = parseFloat(depositAmountValue);
  
      if (isNaN(amount) || amount <= 0) {
        setFormDepositError('Please enter a valid positive amount.');
        return;
      }
  
      try {
        await depositAmount({ amount }); // Calls POST /deposit
  
        setFormDepositSuccess(`Successfully deposited $${amount.toFixed(2)}.`);
        setDepositAmountValue('');
  
        // --- Refresh BOTH balance AND transactions ---
        fetchBalance();
        fetchTransactions(); // <-- UNCOMMENT THIS LINE
  
        // Remove the old confusing error/note message if you had one
        // setFormDepositError('Note: Deposit may not be logged...'); // <-- DELETE this line
  
      } catch (depositError) {
        console.error("Deposit error:", depositError);
        setFormDepositError(depositError.response?.data?.message || 'Deposit failed.');
        // Refresh balance even on failure, in case error was due to outdated UI state
        fetchBalance();
      }
    };
  
    // Also, update or remove the static note in the JSX if you had one:
    // Find this:
    /*
     <p style={{ fontStyle: 'italic', color: 'orange' }}>
         Note: Deposits update balance. They may not be logged in history.
     </p>
    */
    // And DELETE it or change it to something accurate.
  // *** UPDATED Handle Withdrawal Submission ***
  const handleWithdrawSubmit = async (e) => {
      e.preventDefault();
      setFormWithdrawalError('');
      setFormWithdrawalSuccess('');
      const amount = parseFloat(withdrawAmountValue); // Ensure it's a number

      if (isNaN(amount) || amount <= 0) {
          setFormWithdrawalError('Please enter a valid positive amount.');
          return;
      }

      // Note: Frontend check before sending is good UX, but backend check is the authority.
      // We removed the hard block from previous version, letting backend handle 'Insufficient Funds'.

      try {
          // --- Use the correct API function ---
          await withdrawAmount({ amount }); // Calls POST /withdraw

          // Success! Backend handled balance update and logging.
          setFormWithdrawalSuccess(`Successfully withdrew $${amount.toFixed(2)}.`);
          setWithdrawAmountValue(''); // Clear input

          // --- Refresh BOTH balance and transactions ---
          fetchBalance();
          fetchTransactions();

      } catch (err) {
          // Handle errors from the /withdraw endpoint (e.g., insufficient funds)
          console.error("Withdrawal error:", err);
          setFormWithdrawalError(err.response?.data?.message || 'Withdrawal failed.');
          // Refresh balance even on failure, in case error was due to outdated UI state
          fetchBalance();
      }
  };

  // --- JSX Below ---
  return (
    <div>
      <h2>Transactions</h2>

      {/* --- Balance Display --- */}
      <h3>Current Balance</h3>
      {loadingBalance && <p>Loading balance...</p>}
      {errorBalance && <p style={{ color: 'red' }}>Error: {errorBalance}</p>}
      {balance !== null && !loadingBalance && !errorBalance && (
        <p style={{ fontSize: '1.5em', fontWeight: 'bold' }}>
          ${balance.toFixed(2)}
        </p>
      )}
      <hr />

      {/* --- Deposit Form --- */}
      <h3>Make a Deposit</h3>
       {/* Update this note based on whether your /deposit endpoint logs or not */}
       <p style={{ fontStyle: 'italic', color: 'orange' }}>
           Note: Deposits update balance. They may not be logged in history.
       </p>
      <form onSubmit={handleDepositSubmit}>
          <div>
              <label htmlFor="depositAmount">Amount:</label>
              <input type="number" id="depositAmount" value={depositAmountValue}
                     onChange={(e) => setDepositAmountValue(e.target.value)}
                     min="0.01" step="0.01" required />
          </div>
          {formDepositSuccess && <p style={{ color: 'green' }}>{formDepositSuccess}</p>}
          {formDepositError && <p style={{ color: formDepositSuccess ? 'orange' : 'red' }}>{formDepositError}</p>}
          <button type="submit">Deposit</button>
      </form>
      <hr />

      {/* --- UPDATED Withdrawal Form --- */}
      <h3>Make a Withdrawal</h3>
      {/* REMOVED the previous large red warning paragraph */}
      <form onSubmit={handleWithdrawSubmit}>
            <div>
                <label htmlFor="withdrawAmount">Amount:</label>
                <input type="number" id="withdrawAmount" value={withdrawAmountValue}
                       onChange={(e) => setWithdrawAmountValue(e.target.value)}
                       min="0.01" step="0.01" required />
            </div>
            {/* Standard success/error messages */}
            {formWithdrawalSuccess && <p style={{ color: 'green' }}>{formWithdrawalSuccess}</p>}
            {formWithdrawalError && <p style={{ color: 'red' }}>{formWithdrawalError}</p>}
             {/* Updated button text and simpler disabled logic */}
            <button type="submit" disabled={balance === null}>Withdraw</button>
      </form>
      <hr />

      {/* --- Transaction History --- */}
      <h3>History</h3>
      {loadingTransactions && <p>Loading transaction history...</p>}
      {errorTransactions && <p style={{ color: 'red' }}>Error fetching history: {errorTransactions}</p>}
      {!loadingTransactions && transactions.length === 0 && !errorTransactions && (
           <p>No transactions found.</p>
      )}
      {transactions.length > 0 && (
        <ul>
          {transactions.map((tx) => (
             <li key={tx.id || tx.transaction_id}> {/* Use actual unique key */}
                {new Date(tx.transaction_date || tx.created_at || tx.timestamp).toLocaleString()} - {/* Adjust date field */}
                {tx.transaction_type} -
                Amount: ${parseFloat(tx.amount).toFixed(2)}
                {/* REMOVED the "(Logged Only...)" span */}
             </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TransactionsPage;
