import React, { useState, useEffect } from 'react';
import { getBalance, getAccountType } from '../api'; // Import API functions
import useAuth from '../hooks/useAuth';

const DashboardPage = () => {
  const { user } = useAuth();
  const [balance, setBalance] = useState(null);
  const [accountType, setAccountType] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const balanceRes = await getBalance();
        const balanceValue = parseFloat(balanceRes.data.balance);
        setBalance(isNaN(balanceValue) ? null : balanceValue); // ensure number

        const accountTypeRes = await getAccountType();
        setAccountType(accountTypeRes.data.account_type);
      } catch (err) {
        console.error("Dashboard fetch error:", err);
        setError(err.response?.data?.message || 'Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []); // Fetch on component mount

  if (loading) return <p>Loading dashboard...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;

  return (
    <div>
      <h2>Dashboard</h2>
      <p>Welcome, {user?.name}!</p>
      <p>Account Number: {user?.account_no}</p>
      {accountType && <p>Account Type: {accountType}</p>}
      {balance !== null ? (
        <p>Current Balance: ${balance.toFixed(2)}</p>
      ) : (
        <p>Could not retrieve balance.</p>
      )}
      {/* You can add deposit/withdrawal forms or actions here */}
    </div>
  );
};

export default DashboardPage;
